mapper.update:
update
set
SYS_ID = #{SYS_ID}, COMPANY_NAME = #{COMPANY_NAME}, LINKMAN = #{LINKMAN}, PHONE = #{PHONE}, DUTY_PARAGRAPH = #{DUTY_PARAGRAPH}, OPENING_BANK = #{OPENING_BANK}, LICENCE = #{LICENCE}, ACCESSORY = #{ACCESSORY}, BZ = #{BZ}, BANKACCOUNT = #{BANKACCOUNT}, STATUS = #{STATUS}, ACT_ID = #{ACT_ID}, HOSPITAL_ID = #{HOSPITAL_ID}, ISZILIAOQQ = #{ISZILIAOQQ}, FUZEREN = #{FUZEREN}, JINGYINGXUKEZHENG = #{JINGYINGXUKEZHENG}, FARENSHOUQUAN = #{FARENSHOUQUAN}, SHOUQUANWEITUO = #{SHOUQUANWEITUO}, SHENGCHANXUKEZHENG = #{SHENGCHANXUKEZHENG}, CUSTOMER_ID = CUSTOMER_ID where CUSTOMER_ID = #{CUSTOMER_ID}

mapper.select:
select d8.BIANMA BIANMA8, d8.NAME DNAME8,
from f left join d8 on f.LICENCE = d8.BIANMA where 1=1
and (
SYS_ID LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or COMPANY_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or LINKMAN LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%')
)
and SYS_ID=#{pd.CUSTOMER_ID}
and STATUS=#{pd.STATUS}

select d8.BIANMA BIANMA8, d8.NAME DNAME8,
from f left join d8 on f.LICENCE = d8.BIANMA where 1=1
and CUSTOMER_ID=#{pd.CUSTOMER_ID}
